from collections import dequedef solution(bridge_length, weight, truck_weights):    answer = 0    cross = deque([0]*bridge_length)    truck_weights = deque(truck_weights)    total = 0    while cross:        b = cross.popleft()        total -= b        answer += 1        if not truck_weights:            continue        if total + truck_weights[0] <= weight:            a = truck_weights.popleft()            total += a            cross.append(a)        else:            cross.append(0)            return answer